function FormattedDate(props) {
  return <h2>{props.date.toLocaleTimeString()}</h2>
}
class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.setState({date: props.date});
  }
  render() {
    return (
      <FormattedDate date={this.props.date}/>
    )
  }
} 
