//example
function Button({initialState}) {
    const [state, setState] = useState(initialState);
    return (
        <div>
            <h1>{state}</h1>
            <button onClick={() => setState(initialState)}>reset</button>
            <button onClick={() => setState(prevState => prevState - 1)}>-</button>
            <button onClick={() => setState(prevState => prevState + 1)}>+</button>
        </div>
    )
}
