let obj = {dollar: 522.59, dram: 1, rubli: 7.04, euro: 638.56}
//main Component Change
function ChangeInput(props) {
    const value = props.value;

    return <input value={value}></input>
}

function Change() {
    const [value, setValue] = useState(0);
    const [currentChange, setCurrentChange] = useState('dram');

    //handlers
    function handleInputChange(e) {
        setValue(e.target.value);
        setCurrentChange(e.target.nameOfChange);
    }
    const inputs = Object.keys(obj).map((name, index) => {
        return (
            <input nameOfChange={name} key={index} onChange={() => handleInputChange}>{(value * obj[currentChange] / obj[name]).toFixed(2)}</input>
        )
    })
    return (
        <div>
            {inputs}
        </div>
    )
}
