function App1(props) {
    const [click, setClick] = useState(0);
    const [time, setTime] = useState(5);
    // const [result, setResult] = useState([]);

    function tryAgain() {
        if (time === 0) {
            // this.setState((state, props) => ({
            //     time: (state.time = 5),
            //     click: (state.click = 0),
            // }))
            setTime(5);
            setClick(0);
        }
    }
    function clicking() {
        if (time > 0) {
            // this.setState((state, props) => ({ click: ++state.click }))
            setClick(click + 1);
        }
    }
    let timeId = 0;

    useEffect(() => {
        timeId = setInterval(() => {
            if(time > 0) {
                setTime(time - 1);
            }

        }, 1000);
        return () => clearInterval(timeId);
    },[]);

    return (
        <div className="App">
            <header className="App-header">
                <div className="congratulations_block">
                    <p className="congratulations">
                        {click >= 60 ? 'Congratulations' : ''}
                    </p>
                </div>

                <p>Goal - 60 click</p>
                <h1> Time - {time}</h1>
                <h2>Clicks - {click}</h2>
                <button className="button_style" onClick={clicking}>
                    Click me
                </button>
                <button className="button_style" onClick={tryAgain}>
                    Try Again
                </button>
            </header>
        </div>
    )

}
class App extends React.Component {
    constructor(props) {
        super(props)
        // this.resultClick = []
        this.state = { click: 0, time: 5, result:[] }
        this.clicking = this.clicking.bind(this)
        this.tryAgain = this.tryAgain.bind(this)
    }
    componentDidMount() {
        this.timeId = setInterval(() => {
            this.setState((state, props) => {
                if (this.state.time > 0) {
                    return { time: state.time - 1 }
                }
            })
        }, 1000)
    }
    componentWillUnmount() {

        clearInterval(this.timeId)
    }
    clicking() {
        if (this.state.time > 0) {
            this.setState((state, props) => ({ click: ++state.click }))
        }
    }
    tryAgain() {
        if (this.state.time === 0) {
            this.setState((state, props) => ({
                time: (state.time = 5),
                click: (state.click = 0),
            }))
        }
    }
    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <div className="congratulations_block">
                        <p className="congratulations">
                            {this.state.click >= 60 ? 'Congratulations' : ''}
                        </p>
                    </div>

                    <p>Goal - 60 click</p>
                    <h1> Time - {this.state.time}</h1>
                    <h2>Clicks - {this.state.click}</h2>
                    <button className="button_style" onClick={this.clicking}>
                        Click me
                    </button>
                    <button className="button_style" onClick={this.tryAgain}>
                        Try Again
                    </button>
                </header>
            </div>
        )
    }
}

